cp -r -f "/tmp/bar.xml" "/tmp/orangefox/ramdisk/twres/themes/bar.xml";


cd "/twres";
sed "s/1920+144/1920/g" "ui.xml" > "/tmp/ui-temp.xml";
sed "s/1032/960/g" "/tmp/ui-temp.xml" > "/tmp/ui-temp1.xml";
sed "s/1\"/0\"/g" "themes/bar.xml" > "/tmp/bar.xml";

cp -r -f "/tmp/ui-temp1.xml" "/tmp/orangefox/ramdisk/twres/ui.xml";
cp -r -f "/tmp/ui-temp1.xml" "/twres/ui.xml";

cp -r -f "/tmp/bar.xml" "/twres/themes/bar.xml";
